CREATE TABLE users (
  user_id serial primary key,
  user_fname varchar(20) NOT NULL,
  user_lname varchar(20) NOT NULL,
  user_address text NOT NULL,
  user_phone varchar(10) NOT NULL,
  user_email varchar(50) NOT NULL,
  user_password varchar(50) NOT NULL
);


CREATE TABLE medicine (
  medicine_id serial primary key,
  medicine_title varchar(250) NOT NULL,
  medicine_brand varchar(250) NOT NULL,
  medicine_cat varchar(15) NOT NULL,
  medicine_details text NOT NULL,
  medicine_tags varchar(250) NOT NULL,
  medicine_image varchar(250) NOT NULL,
  medicine_quantity int NOT NULL,
  medicine_price int NOT NULL,
  mfg_date date not null,
  exp_date date not null
);



CREATE TABLE orders (
	order_id serial primary key,
  	order_date date not null,
	user_id int references medicine(medicine_id) on delete cascade,
	total_amt int,
	delivery_fees int,
	order_status int default 0
);

create table orders_details(
	order_id int references orders(order_id) on delete cascade,
	medicine_id int references medicine(medicine_id) on delete cascade,
  	order_quantity int NOT NULL
);
  	
CREATE TABLE admin (
  admin_id serial primary key,
  admin_email varchar(50) NOT NULL,
  admin_fname varchar(20) NOT NULL,
  admin_lname varchar(20) NOT NULL,
  admin_password varchar(50) NOT NULL
);


